import React, {Component} from 'react';

class EditUserForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            name: "",
            telephone: "",
            permissions: "",
            statusSave: true
        }
    }

    isChange = (event) => {
        console.log(this.props.userDataBfEdit.name);
        this.setState({[event.target.name]: event.target.value});
    };
    onClickSave = () => {
        if(this.state.statusSave){
            return this.props.getNewDataUser(this.state.name, this.state.telephone, this.state.permissions)
        }
        return this.props.getNewDataUser(this.props.userDataBfEdit.name, this.props.userDataBfEdit.name, this.props.userDataBfEdit.name)
    };
    displayForm = () => {
        if (this.props.editStatus) {
            return (
                <div className="col">
                    <form>
                        <div className="card border-success mt-3" style={{maxWidth: "18rem"}}>
                            <div className="card-header bg-transparent border-success text-center">
                                <strong>Sửa thông tin</strong>
                            </div>
                            <div className="card-body text-success">
                                <div className="from-group">
                                    <div>
                                        <label>Tên user</label>
                                        <input
                                            className="form-control"
                                            type="text"
                                            name="name"
                                            placeholder="Nhập tên user"
                                            defaultValue={this.props.userDataBfEdit.name}
                                            onChange={(event) => this.isChange(event)}
                                        />
                                    </div>
                                    <div className="mt-3">
                                        <label>Điện thoại</label>
                                        <input
                                            className="form-control"
                                            type="text"
                                            name="telephone"
                                            placeholder="Nhập điện thoại"
                                            defaultValue={this.props.userDataBfEdit.telephone}
                                            onChange={(event) => this.isChange(event)}
                                        />
                                    </div>
                                    <div className="mt-3">
                                        <label>Phân quyền</label>
                                        <select className="custom-select mr-sm-2"
                                                name="permissions"
                                                defaultValue={this.props.userDataBfEdit.permissions}
                                                onChange={(event) => this.isChange(event)}
                                        >
                                            <option>Choose...</option>
                                            <option value="1">Admin</option>
                                            <option value="2">Moderator</option>
                                            <option value="3">Normal</option>
                                        </select>
                                    </div>
                                    <div className="mt-3 text-center">
                                        <button type="reset" className="btn btn-outline-success btn-block"
                                                onClick={() => this.onClickSave()}
                                        >
                                            Lưu
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            )
        }
    }

    render() {
        return (
            <div>
                {this.displayForm()}
            </div>
        );
    }
}

export default EditUserForm;
