import React, {Component} from 'react';
import '../stylesheets/App.css';
import Header from "./Header";
import Search from "./Search";
import TableData from "./TableData";
import AddUser from "./AddUser";
import data from "../data.json";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: false,
            data: data,
            dataUser: ""
        }
    }

    getNewDataUser = (id, name, tel, per) => {
        let item = {
            id: id,
            name: name,
            telephone: tel,
            permissions: per
        };
        this.setState({dataUser: item});
    };
    isCheck = () => {
        this.setState({status: !this.state.status});
    };
    getTextSearch = (dl) => {
        this.setState({searchText: dl.toLowerCase().trim()});
    };
    getArrayTextSearch = () => {
        let arr = [];
        data.forEach(item => {
            if (item.name.toLowerCase().indexOf(this.state.searchText) !== -1) {
                arr.push(item);
            }
        });
        return arr;
    };

    render() {
        return (
            <div>
                <Header/>
                <div className="searchForm">
                    <div className="container">
                        <div className="row">
                            <Search
                                status={() => this.isCheck()}
                                displayButton={this.state.status}
                                getTextSearch={dl => this.getTextSearch(dl)}
                            />
                            <div className="col-12">
                                <hr/>
                            </div>
                            <TableData data={this.getArrayTextSearch()}/>
                            <AddUser displayForm={this.state.status}
                                     getNewDataUser={(id, name, tel, per) => this.getNewDataUser(id, name, tel, per)}
                            />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
