import React, {Component} from 'react';
import '../stylesheets/App.css';
import Header from "./Header";
import Search from "./Search";
import TableData from "./TableData";
import AddUser from "./AddUser";
import data from "../data.json";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: false,
            data: data
        }
    }

    isCheck = () => {
        this.setState({status: !this.state.status});
    };
    getTextSearch = (dl) => {
        this.setState({searchText: dl});
    };

    render() {
        var arr = [];
        var x = data.find(item => console.log(item.name.indexOf(this.state.searchText)));
        arr.push(x);
        console.log(arr);
        return (
            <div>
                <Header/>
                <div className="searchForm">
                    <div className="container">
                        <div className="row">
                            <Search
                                status={() => this.isCheck()}
                                displayButton={this.state.status}
                                getTextSearch={dl => this.getTextSearch(dl)}
                            />
                            <div className="col-12">
                                <hr/>
                            </div>
                            <TableData data={this.state.data}/>
                            <AddUser displayForm={this.state.status}/>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
