import React, {Component} from 'react';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: false
        }
    }

    displayButton = () => {
      if(this.state.status){
          return <div className="btn btn-success btn-block">Đóng lại</div>
      }
        return <div className="btn btn-success btn-block">Thêm mới</div>
    };
    displayForm = () => {

    };
    render() {
        return (
            <div className="col-3">

                {this.displayButton()}
                <div className="card border-success mt-3" style={{maxWidth: "18rem"}}>
                    <div className="card-header bg-transparent border-success text-center">
                        <strong>Thêm mới</strong>
                    </div>
                    <div className="card-body text-success">
                        <div className="from-group">
                            <div>
                                <label>Tên user</label>
                                <input
                                    className="form-control"
                                    type="text"
                                    name="name"
                                    placeholder="Nhập tên user"
                                />
                            </div>
                            <div className="mt-3">
                                <label>Điện thoại</label>
                                <input
                                    className="form-control"
                                    type="text"
                                    name="telephone"
                                    placeholder="Nhập điện thoại"
                                />
                            </div>
                            <div className="mt-3">
                                <label >Phân quyền</label>
                                <select className="custom-select mr-sm-2">
                                    <option defaultValue >Choose...</option>
                                    <option value="admin">Admin</option>
                                    <option value="moderator">Moderator</option>
                                    <option value="normal">Normal</option>
                                </select>
                            </div>
                            <div className="mt-3 text-center">
                                <button className="btn btn-outline-success btn-block">
                                    Thêm mới
                                </button>
                            </div>
                        </div>
                    </div>
                    <div className="card-footer bg-transparent border-success">Footer</div>
                </div>
            </div>
        );
    }
}

export default AddUser;
