import React, {Component} from 'react';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            name: "",
            telephone: "",
            permissions: "",
            data: ""
        }

    }

    // submitForm = (event) => {
    //     event.preventDefault();
    //     this.setState({isRedirect: true});
    // };
    isChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
        var item = {
            id: this.state.id,
            name: this.state.name,
            telephone: this.state.telephone,
            permissions: this.state.permissions
        };
        this.props.getNewDataUser(item);
    };
    displayForm = () => {
        if (this.props.displayForm) {
            return (
                <div className="col">
                    <div className="card border-success mt-3" style={{maxWidth: "18rem"}}>
                        <div className="card-header bg-transparent border-success text-center">
                            <strong>Thêm mới</strong>
                        </div>
                        <div className="card-body text-success">
                            <div className="from-group">
                                <div>
                                    <label>Tên user</label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        name="name"
                                        placeholder="Nhập tên user"
                                        onChange={(event) => this.isChange(event)}
                                    />
                                </div>
                                <div className="mt-3">
                                    <label>Điện thoại</label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        name="telephone"
                                        placeholder="Nhập điện thoại"
                                        onChange={(event) => this.isChange(event)}
                                    />
                                </div>
                                <div className="mt-3">
                                    <label>Phân quyền</label>
                                    <select className="custom-select mr-sm-2"
                                            onChange={(event) => this.isChange(event)}
                                            name="permissions"
                                            defaultValue={this.state.permissions}
                                    >
                                        <option>Choose...</option>
                                        <option value="1">Admin</option>
                                        <option value="2">Moderator</option>
                                        <option value="3">Normal</option>
                                    </select>
                                </div>
                                <div className="mt-3 text-center">
                                    <button type="submit" className="btn btn-outline-success btn-block"
                                            onClick={(item) => this.props.getNewDataUser()}>
                                        Thêm mới
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }
    };

    render() {
        // if(this.state.isRedirect){
        //     return <Redirect to="/"/>
        // }
        return (
            <div>
                {this.displayForm()}
            </div>
        );
    }
}

export default AddUser;
