import React, {Component} from 'react';

class Search extends Component {
    displayButton = () => {
        if (this.props.displayButton) {
            return <div className="btn btn-success btn-block add" onClick={() => this.props.status()}>Đóng lại</div>
        }
        return <div className="btn btn-success btn-block add" onClick={() => this.props.status()}>Thêm mới</div>
    };

    render() {
        console.log()
        return (
            <div>
                <div className="col-12">
                    <div className="form-group">
                        <div className="btn-group">
                            <input
                                className="form-control inputSearch"
                                type="text"
                                name="seach"
                                placeholder="Nhập từ khoá"
                                onChange={(event) => this.props.isChange(event)}
                            />
                            <button className="btn btn-primary">Tìm</button>
                            {this.displayButton()}
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default Search;
