import React, {Component} from 'react';
import '../stylesheets/App.css';
import Header from "./Header";
import Search from "./Search";
import TableData from "./TableData";
import AddUserForm from "./AddUserForm";
import data from "../data.json";
import EditUserForm from "./EditUserForm";
import {v4 as uuidv4} from 'uuid';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: false,
            data: data,
            searchText: "",
            editStatus: false,
            userDataBfEdit: {}
        }
    }

    getInfoEditUser = (item) => {
        var objectUser = this.state.data.find(user => user.id === parseInt(item.id));
        objectUser.name = item.name;
        objectUser.telephone = item.telephone;
        objectUser.permissions = item.permissions;
        console.log(objectUser);
        // this.state.data.push(newUser);
        this.setState({data: this.state.data});
    }
    onClickEditUser = () => {
        this.setState({editStatus: !this.state.editStatus});
    };
    editUser = (user) => {
        console.log("Ket noi thanh cong");
        // console.log(user);
        this.setState({userDataBfEdit: user});
    };
    getNewDataUser = (name, tel, per) => {
        let item = {
            id: uuidv4(),
            name: name,
            telephone: tel,
            permissions: per
        };
        this.state.data.push(item);
        this.setState({data: this.state.data});
    };
    isCheck = () => {
        this.setState({status: !this.state.status});
    };
    getTextSearch = (dl) => {
        this.setState({searchText: dl.toLowerCase().trim()});
    };
    getArrayTextSearch = () => {
        let arr = [];
        data.forEach(item => {

            if (item.name.toLowerCase().indexOf(this.state.searchText) !== -1) {
                arr.push(item);
            }
        });
        return arr;
    };

    render() {
        // console.log(this.state.userDataBfEdit)
        return (
            <div>
                <Header/>
                <div className="searchForm">
                    <div className="container">
                        <div className="row">
                            <Search
                                status={() => this.isCheck()}
                                displayForm={this.state.status}
                                getTextSearch={dl => this.getTextSearch(dl)}
                            />
                            <div className="col-12">
                                <hr/>
                            </div>
                            <TableData data={this.getArrayTextSearch()}
                                       editUser={(user) => this.editUser(user)}
                                       onClickEditUser={() => this.onClickEditUser()}
                            />
                            <AddUserForm displayForm={this.state.status}
                                         getNewDataUser={(name, tel, per) => this.getNewDataUser(name, tel, per)}
                            />
                            <EditUserForm onClickEditUser={() => this.onClickEditUser()}
                                          editStatus={this.state.editStatus}
                                          userDataBfEdit={this.state.userDataBfEdit}
                                          getInfoEditUser={(item) => this.getInfoEditUser(item)}

                            />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
