import React, {Component} from 'react';

class EditUserForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            name: "",
            telephone: "",
            permissions: "",
            isChange: false
        }
    }


    isChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value,
            isChange: true
        });
    };
    editClick = () => {
        if (!this.state.isChange) {
            this.setState({
                id: this.props.userEditObject.id,
                name: this.props.userEditObject.name,
                telephone: this.props.userEditObject.telephone,
                permissions: this.props.userEditObject.permissions
            });

        }
        let temp = this.state;
        if (this.state.isChange) {
            let item = {
                id: temp.id,
                name: temp.name,
                telephone: temp.telephone,
                permissions: temp.permissions
            };
            this.props.getUserEditInfo(item);
        }


        this.props.changeEditUserStatus();

    };
    displayForm = () => {
        if (this.props.editUserStatus) {
            return (
                <div className="col">
                    <form>
                        <div className="card border-success mt-3" style={{maxWidth: "18rem"}}>
                            <div className="card-header bg-transparent border-success text-center">
                                <strong>Sửa thông tin</strong>
                            </div>
                            <div className="card-body text-success">
                                <div className="from-group">
                                    <div>
                                        <label>Tên user</label>
                                        <input
                                            className="form-control"
                                            type="text"
                                            name="name"
                                            placeholder="Nhập tên user"
                                            defaultValue={this.props.userEditObject.name}
                                            onChange={(event) => this.isChange(event)}
                                        />
                                    </div>
                                    <div className="mt-3">
                                        <label>Điện thoại</label>
                                        <input
                                            className="form-control"
                                            type="text"
                                            name="telephone"
                                            placeholder="Nhập điện thoại"
                                            defaultValue={this.props.userEditObject.telephone}
                                            onChange={(event) => this.isChange(event)}
                                        />
                                    </div>
                                    <div className="mt-3">
                                        <label>Phân quyền</label>
                                        <select className="custom-select mr-sm-2"
                                                name="permissions"
                                                defaultValue={this.props.userEditObject.permissions}
                                                onChange={(event) => this.isChange(event)}
                                        >
                                            <option>Choose...</option>
                                            <option value="1">Admin</option>
                                            <option value="2">Moderator</option>
                                            <option value="3">Normal</option>
                                        </select>
                                    </div>
                                    <div className="mt-3 text-center">
                                        <button type="button" className="btn btn-outline-success btn-block"
                                                onClick={() => this.editClick()}
                                        >
                                            Lưu
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            )
        }
    };


    render() {
        return (
            <div>
                {this.displayForm()}
            </div>
        );
    }
}

export default EditUserForm;
